import React, { useState, useEffect } from "react";
import { Container, Table, Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPrint, faGift, faEnvelope } from "@fortawesome/free-solid-svg-icons";
import { useHistory } from 'react-router-dom';

function PaymentReceived({ products }) {
  const [cartItems, setCartItems] = useState([...products]);
  const history = useHistory();

  useEffect(() => {
    setCartItems([...products]);
  }, [products]);

  const handleSale = () => {
    history.push("/sales-summary");
  };

  return (
    <div className="checkout-container">
      <h1 className="text-center pt-5 pb-5">Payment Received</h1>
      <Table>
        <div className="pt-5 col-12 row">
          <a className="col-6 printIcon" style={{ textDecoration: 'none' }}>
            Print
            <FontAwesomeIcon icon={faPrint} className="mr-1" />
          </a>
          <a className="col-6 giftIcon" style={{ textDecoration: 'none' }}>
            <FontAwesomeIcon icon={faGift} className="mr-1" />
            Gift Receipt
          </a>
        </div>
        <tbody>
          <tr className="col-12">
            <td className="border-bottom-0 pt-5 col-12">
            <form className="form-inline d-inline-flex col-12">
  <div className="form-group col-4 offset-1">
    <div className="input-group">
      <div className="input-group-prepend">
        {/* <span className="input-group-text">
          <FontAwesomeIcon icon={faEnvelope} style={{ marginRight: "8px" }} />
        </span> */}
      </div>
      <input
        type="email"
        className="form-control ml-1"
        id="Email Address2"
        placeholder="Email Address"
      />
    </div>
  </div>
  <div className="col-6 offset-1">
    <button type="submit" className="btn btn-primary mr-1">
      Email Receipt
    </button>
  </div>
</form>
            </td>
          </tr>
        </tbody>
      </Table>
      <div className="checkout-summary" style={{ marginTop: "400px" }}>
        <div className="pay-details">
          <Button
            className="pay-button col-12 text-center"
            variant="primary"
            block
            onClick={handleSale}
          >
            Complete Sale (ESC)
          </Button>
        </div>
      </div>
    </div>
  );
}

export default PaymentReceived;
