import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";
import { useLocation } from "react-router-dom";

const SaleSummary = ({ addToSale, history }) => {
  const handleSale = () => {
    const product = { name: "Sale", price: 0 };
    addToSale(product);
    history.push("/");
  };
  const location = useLocation();

  useEffect(() => {
    // Do something when the location changes
  }, [location]);
  return (
    <div className="sale-summary-container">
      <h2>Sale Summary</h2>
      <button onClick={handleSale}>Confirm Sale</button>
    </div>
  );
};

export default withRouter(SaleSummary);
