import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Container from "react-bootstrap/Container";
import ProductsTable from "./Components/ProductsTable";
import Checkout from "./Components/Checkout/Checkout";
import SaleSummary from "./Components/SaleSummary"; // import the SaleSummary component
import NavBar from "./NavBar";

function App() {
  return (
    <Router>
      <NavBar firstName="John" lastName="Doe" />
      <Container className="main-container">
        <Switch>
          <Route exact path="/">
            <div className="products-container">
              <ProductsTable products={products} addToSale={addToSale} />
            </div>
            <div className="checkout-container">
              <Checkout
                products={cartItems}
                setCartItems={setCartItems}
                allProducts={allProducts}
              />
            </div>
          </Route>
          <Route exact path="/sales-summary">
            <SaleSummary addToSale={addToSale} />
          </Route>
        </Switch>
      </Container>
    </Router>
  );
}
export default App;
