import React, { useState, useEffect } from "react";
import { Container, Table, Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { useHistory } from 'react-router-dom';

function PaymentReceived({ products }) {
  const [cartItems, setCartItems] = useState([...products]);
  const history = useHistory();

  useEffect(() => {
    setCartItems([...products]);
  }, [products]);

  const handleProductDelete = (productId) => {
    setCartItems((prevCartItems) => {
      const updatedItems = prevCartItems.filter(
        (item) => item.id !== productId
      );
      console.log(updatedItems);
      return updatedItems;
    });
  };

  const handleQuantityChange = (id, quantity) => {
    console.log(
      `handleQuantityChange called with id=${id} quantity=${quantity}`
    );
    const updatedItems = cartItems.map((item) => {
      if (item.id === id) {
        console.log(
          `Updating quantity of item ${id} from ${item.quantity} to ${quantity}`
        );
        return {
          ...item,
          quantity: quantity
        };
      }
      return item;
    });
    console.log(`Updated cart items: ${JSON.stringify(updatedItems)}`);
    setCartItems(updatedItems);
  };

  const totalItems = cartItems.reduce((acc, item) => {
    if (typeof item.quantity === "number") {
      return acc + item.quantity;
    } else {
      console.error(
        `Invalid quantity value ${item.quantity} for product ${item.id}`
      );
      return acc;
    }
  }, 0);

  const totalPrice = cartItems.reduce(
    (acc, item) => acc + (parseFloat(item.price) || 0) * (item.quantity || 1), // set quantity and price to 1 and 0 if null
    0
  );

  const handleSale = () => {
    history.push("/sales-summary");
  };

  return (
    <div className="checkout-container">
      <h1 className="text-center pt-5">Payment Received</h1>
      <Table>
        <tbody>
        <tr key={item.id}>
              <form class="form-inline">
                <div class="form-group mb-2">
                  <label for="staticEmail2" class="sr-only">Email</label>
                  <input type="text" readonly class="form-control-plaintext" id="staticEmail2" value="email@example.com" />
                </div>
                <div class="form-group mx-sm-3 mb-2">
                  <label for="inputPassword2" class="sr-only">Password</label>
                  <input type="password" class="form-control" id="inputPassword2" placeholder="Password" />
                </div>
                <button type="submit" class="btn btn-primary mb-2">Confirm identity</button>
              </form>
              <td style={{ width: "1%" }}>
                <Button
                  style={{ padding: "0 5px" }}
                  onClick={() => handleProductDelete(item.id)}
                >
                  <FontAwesomeIcon icon={faTrash} />
                </Button>
              </td>
            </tr>
        </tbody>
      </Table>
      <div className="checkout-summary" style={{ marginTop: "400px" }}>
        <div className="pay-details">
          <Button
            className="pay-button col-12 text-center"
            variant="primary"
            block
            onClick={handleSale}
          >
            Complete Sale (ESC)
          </Button>
        </div>
      </div>
    </div>
  );
}

export default PaymentReceived;
