import React, { useState } from "react";
import { Container, Table, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

function SaleSummary({ addToSale }) {
  const [products] = useState([
    { id: 1, name: "Product 1", price: 10.0 },
    { id: 2, name: "Product 2", price: 20.0 },
    { id: 3, name: "Product 3", price: 30.0 }
  ]);

  const handleRowClick = (productId) => {
    const productToAdd = products.find((product) => product.id === productId);
    addToSale(productToAdd);
  };

  const totalItems = products.length;
  const totalPrice = products.reduce((total, product) => total + product.price, 0);

  return (
    <div className="p-3">
      <h2 className="pb-4">Sales Summary</h2>
      <Table className="table table-borderless table-hover">
        <tbody className="fw-bold">
          {products.map((product, index) => (
            <tr className="productTr " key={product.id} onClick={() => handleRowClick(product.id)}>
              <td>{index + 1}</td>
              <td>
                <span>{product.name}</span>
              </td>
              <td className="text-right">{product.price.toFixed(2)}</td>
            </tr>
          ))}
          <tr className="saleTotalTr fw-normal row-auto">
            <td colSpan="2" className="text-right" style={{ letterSpacing: "2px" }}>
              <b>SALE TOTAL</b> {totalItems} items
            </td>
            <td className="text-right">${totalPrice.toFixed(2)}</td>
          </tr>
        </tbody>
      </Table>
      <div className="d-flex justify-content-end">
        <Link to="/">
          <Button variant="primary">Back</Button>
        </Link>
      </div>
    </div>
  );
}

export default SaleSummary;
