import React, { useState, useEffect } from "react";
import { Container, Table, Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { useHistory } from 'react-router-dom';

function PaymentReceived({ products }) {
  const [cartItems, setCartItems] = useState([...products]);
  const history = useHistory();

  useEffect(() => {
    setCartItems([...products]);
  }, [products]);

  const handleSale = () => {
    history.push("/sales-summary");
  };

  return (
    <div className="checkout-container">
      <h1 className="text-center pt-5">Payment Received</h1>
      <Table>
        <tbody>
          <tr>
            <td className="col border-bottom-0">
              <form className="form-inline row">
                <div className="form-group mx-sm-3 mb-2">
                  <label htmlFor="inputPassword2" className="sr-only">
                    Email Address
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="inputPassword2"
                    placeholder="Password"
                  />
                </div>
                <button type="submit" className="btn btn-primary col-4">
                  Email Receipt
                </button>
              </form>
            </td>
          </tr>
        </tbody>
      </Table>
      <div className="checkout-summary" style={{ marginTop: "400px" }}>
        <div className="pay-details">
          <Button
            className="pay-button col-12 text-center"
            variant="primary"
            block
            onClick={handleSale}
          >
            Complete Sale (ESC)
          </Button>
        </div>
      </div>
    </div>
  );
}

export default PaymentReceived;
