import React from "react";
import { withRouter } from "react-router-dom";
import { useLocation } from "react-router-dom";

const SaleSummary = ({ addToSale, history }) => {
  const handleSale = () => {
    addToSale();
    history.push("/");
  };

  return (
    <div className="sale-summary-container">
      <h2>Sale Summary</h2>
      <button onClick={handleSale}>Confirm Sale</button>
    </div>
  );
};

export default withRouter(SaleSummary);
