import React, { useState } from "react";
import { Container, Table, Button } from "react-bootstrap";

function ProductsTable({ addToSale }) {
  const [products] = useState([
    { id: 1, name: "Product 1", price: 10.0 },
    { id: 2, name: "Product 2", price: 20.0 },
    { id: 3, name: "Product 3", price: 30.0 }
  ]);

  const handleRowClick = (productId) => {
    const productToAdd = products.find((product) => product.id === productId);
    addToSale(productToAdd);
  };

  return (
    <div className="p-3">
      <h2 className="pb-4">Products</h2>
      <Table striped hover>
        <thead>
          <tr>
            <th style={{ paddingLeft: "0" }}>Product</th>
            <th className="text-right">Price</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id} onClick={() => handleRowClick(product.id)}>
              <td>
                <img
                  src="https://via.placeholder.com/45"
                  alt="product"
                  style={{
                    width: "40px",
                    height: "40px",
                    border: "1px solid grey"
                  }}
                />
                <span style={{ paddingLeft: "6px" }}>{product.name}</span>
              </td>
              <td className="text-right">${product.price.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
        <tfoot className="border-bottom-2">
          <tr className="text-right">
            <td colSpan="2" className="pt-3 pr-0">
              <Button variant="primary">Create New Product</Button>
            </td>
          </tr>
        </tfoot>
      </Table>
    </div>
  );
}

export default ProductsTable;
