import React, { useState, useEffect } from "react";
import { Container, Table, Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPrint, faGift, faEnvelope  } from "@fortawesome/free-solid-svg-icons";
import { useHistory } from 'react-router-dom';

function PaymentReceived({ products }) {
  const [cartItems, setCartItems] = useState([...products]);
  const history = useHistory();

  useEffect(() => {
    setCartItems([...products]);
  }, [products]);

  const handleSale = () => {
    history.push("/sales-summary");
  };

  return (
    <div className="checkout-container">
      <h1 className="text-center pt-5 pb-5">Payment Received</h1>
      <Table>
        <div className="pt-5 col-12 row">
          <div className="col-6 d-inline-block text-right">
            <FontAwesomeIcon icon={faPrint} className="mr-1" />
            Print
          </div>
          <div className="col-6 d-inline-block text-left">
            <FontAwesomeIcon icon={faGift} className="mr-1" />
            Gift Receipt
          </div>
        </div>
        <tbody>
          <tr>
            <td className="border-bottom-0 pt-5">
              <form className="form-inline d-inline-flex col-12">
                <div className="form-group mx-sm-3 col-6">
                  <div className="input-group-prepend">

                  </div>
                  <label htmlFor="inputPassword2" className="sr-only">
                    Email Address
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="Email Address2"
                    placeholder="Email Address"
                  />
                </div>
                <button type="submit" className="btn btn-primary col-4">
                  Email Receipt
                </button>
              </form>
            </td>
          </tr>
        </tbody>
      </Table>
      <div className="checkout-summary" style={{ marginTop: "400px" }}>
        <div className="pay-details">
          <Button
            className="pay-button col-12 text-center"
            variant="primary"
            block
            onClick={handleSale}
          >
            Complete Sale (ESC)
          </Button>
        </div>
      </div>
    </div>
  );
}

export default PaymentReceived;
