import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Container from "react-bootstrap/Container";
import ProductsTable from "./Components/ProductsTable";
import Checkout from "./Components/Checkout/Checkout";
import SaleSummary from "./Components/SaleSummary";
import NavBar from "./NavBar";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [allProducts] = useState([
    { id: 1, name: "Product 1", price: 9.99 },
    { id: 2, name: "Product 2", price: 14.99 },
    { id: 3, name: "Product 3", price: 19.99 }
  ]);
  const [products, setProducts] = useState([...allProducts]);
  const [cartItems, setCartItems] = useState([]);

  const addToSale = (product) => {
    setCartItems((prevCartItems) => {
      const itemIndex = prevCartItems.findIndex(
        (item) => item.id === product.id
      );

      if (itemIndex !== -1) {
        // If the item is already in the cart, update its quantity
        const updatedItems = [...prevCartItems];
        updatedItems[itemIndex] = {
          ...updatedItems[itemIndex],
          quantity: updatedItems[itemIndex].quantity + 1
        };
        return updatedItems;
      } else {
        // If the item is not yet in the cart, add it with a quantity of 1
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };

  return (
    <div className="app-container">
      <Router>
        <NavBar firstName="John" lastName="Doe" />
        <div className="main-container pt-4">
          <Switch>
            <Route exact path="/">
              <div className="row">
                <div className="col-md-6 offset-md-2 pr-3">
                  <div className="products-container">
                    <ProductsTable products={products} addToSale={addToSale} />
                  </div>
                </div>
                <div className="col-md-4 pl-3 pr-0">
                  <div className="checkout-container" style={{ marginRight: "24px" }}>
                    <Checkout
                      products={cartItems}
                      setCartItems={setCartItems}
                      allProducts={allProducts}
                    />
                  </div>
                </div>
              </div>
            </Route>
            <Route exact path="/sales-summary">
              <SaleSummary addToSale={addToSale} />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
};

export default App;
